import React, { useState, useEffect } from "react";
import './sliderImage.styles.css'

import ImageMark from '../../image/imageMark.jpeg'; 
import ImageDesemond from '../../image/image-Desmond.jpeg'; 
import ImageGeneral from '../../image/image-General.jpg'; 
import ImageAkonto from '../../image/image-Akonto.jpg'; 


const Slider = () => {
  const [imageIndex, setImageIndex] = useState(0);
  const images = [ImageMark, ImageDesemond, ImageGeneral, ImageAkonto];
  const image = images[imageIndex];

  const slideForward = () => {
    const newIndex = imageIndex + 1;
    if (newIndex < images.length) {
      setImageIndex(newIndex);
    }
  };

  const slideBackward = () => {
    const newIndex = imageIndex - 1;
    if (newIndex >= 0) {
      setImageIndex(newIndex);
    }
  };

  useEffect(() => {
    const interval = setInterval(() => {
      slideForward();
    }, 1000);
    return () => clearInterval(interval);
  });

  return (
    <div className="slider-image-container">
      <button onClick={slideBackward} className='left-arrow '>{"<"}</button>
        <img src={image} alt={image} className= "image"/>
      <button onClick={slideForward} className="right-arrow ">{">"}</button>
    </div>
  );
};

export default Slider;
